import React, { useState, useEffect } from 'react'
import axios from 'axios'
import { useNavigate } from "react-router-dom";

export default function Profile (){
  const [name, setName] = useState('') //State that stores the name of the user.
  const navigate = useNavigate()

  const instance = axios.create({
    baseURL: 'http://localhost:8000'
  })

  useEffect(async ()=>{// Get the name of the user or redirect to /signup
    const res = await instance.get('/profile')
    setName(await res.data.fullname)  

    console.log(res)
  }, [instance]) 

  return(
    <>
      <h1 className='text-center primary-text'>Welcome {name}!</h1>
    </>
  )
}
