import React, { useEffect, useState } from 'react'
import { NavLink ,useNavigate } from 'react-router-dom'
import axios from 'axios'
import { Dropdown, Button } from "react-bootstrap";

export default function Profile (){
  const [name, setName] = useState('')
  const [numberContacts, setNumberContacts] = useState(0);
  const navigate = useNavigate()

  const instance = axios.create({
    baseURL: 'http://localhost:8000'
  })

  useEffect(()=>{ // Get the user name or redirect to sign in if not authenticated.
    const getName = async ()=>{
      const res = await instance.get('/signin')

      if (res.data.fullname === undefined) {
        navigate('/signin')
      }
      else {
        if (res.data.fullname === undefined) setName(res.data.fullname)
        else setName(res.data.fullname)
      }
    }
    const getContacts = async ()=>{
      const user = await instance.get('/signin')
      await instance.post('/contacts', await user.data)
      const res = await instance.get('/contacts');
      setNumberContacts(res.data.length)
    }
    getName()
    getContacts()
  }, [instance, navigate])

  return(
    <>
      <header className="d-flex justify-content-between text-start p-3" style={{ backgroundColor: "rgb(120, 120, 140)" }}>
        {name}
        <Dropdown>
          <Dropdown.Toggle className='dropdown-toggle border-0'>
            <i className="fa-solid fa-bars text-dark"></i>
          </Dropdown.Toggle>
          <Dropdown.Menu className='dropdown-menu'>
            <section className='m-2 ms-3'>
              <NavLink 
                to='/profile' 
                className={({ isActive }) => "text-dark " + (isActive ? " border-bottom border-dark" : null)}
              >
                  Profile
              </NavLink>
            </section>
            <section className='m-2 ms-3'>
              <NavLink 
                to='/contacts' 
                className={({ isActive }) => "text-dark " + (isActive ? "selected" : null)}
              >
                  Contacts
              </NavLink>
            </section>
            <section className='m-2 ms-3'>
              <NavLink 
                to='/signout' 
                className={({ isActive }) => "text-dark " + (isActive ? "selected" : null)}
              >
                  Sign out
              </NavLink>
            </section>
          </Dropdown.Menu>
        </Dropdown>
      </header>
      <h1 className='text-center primary-text m-5'>Welcome {name}!</h1>
      <main className='text-center'>
        <span className='primary-text my-auto' style={{fontSize: '35px'}}>You have {numberContacts} contacts</span>
        <Button className='d-block mx-auto mt-5'>Go see your contacts!</Button>
      </main>
    </>
  )
}
