import React, { useEffect, useState } from 'react'
import { NavLink ,useNavigate } from 'react-router-dom'
import axios from 'axios'
import { Dropdown, Container } from "react-bootstrap";

export default function Contacts({name}) {
  const [contactsList, setContactsList] = useState([])
  const navigate = useNavigate()

  const instance = axios.create({
    baseURL: 'http://localhost:8000'
  })

  useEffect(()=>{
    const getContacts = async ()=>{
      const res = await instance.get('/contacts');
      await setContactsList(res.data)
    }
    getContacts()
  }, [instance])


  return (
    <>
      <header className="d-flex justify-content-between text-start p-3" style={{ backgroundColor: "rgb(120, 120, 140)" }}>
        <NavLink to='/profile' className='text-dark'>
          {name}
        </NavLink>
        <Dropdown>
          <Dropdown.Toggle className='dropdown-toggle border-0'>
            <i className="fa-solid fa-bars text-dark"></i>
          </Dropdown.Toggle>
          <Dropdown.Menu className='dropdown-menu'>
            <section className='m-2 ms-3'>
              <NavLink 
                to='/profile' 
                className={({ isActive }) => "text-dark " + (isActive ? " border-bottom border-dark" : null)}
              >
                  Profile
              </NavLink>
            </section>
            <section className='m-2 ms-3'>
              <NavLink 
                to='/contacts' 
                className={({ isActive }) => "text-dark " + (isActive ? "selected" : null)}
              >
                  Contacts
              </NavLink>
            </section>
            <section className='m-2 ms-3'>
              <NavLink 
                to='/signout' 
                className={({ isActive }) => "text-dark " + (isActive ? "selected" : null)}
              >
                  Sign out
              </NavLink>
            </section>
          </Dropdown.Menu>
        </Dropdown>
      </header>
      <Container>
        <ul>
          {/* {contactsList.map((contact, id)=>(
            <li key={id}>{contact.fullname}</li>
          ))} */}
        </ul>
      </Container>
    </>
  )
}
