import React, { useEffect, useState } from 'react'
import { Link ,useNavigate } from 'react-router-dom'
import axios from 'axios'
import { Dropdown } from "react-bootstrap";

export default function Profile (){
  const [name, setName] = useState('')
  const navigate = useNavigate()

  const instance = axios.create({
    baseURL: 'http://localhost:8000'
  })

  useEffect(()=>{ // Get the user name or redirect to sign in if not authenticated.
    const getName = async ()=>{
      const res = await instance.get('/signin')

      if (res.data.fullname === undefined) {
        navigate('/signin')
      }
      else {
        if (res.data.fullname[0].fullname === undefined) setName(res.data.fullname)
        else setName(res.data.fullname[0].fullname)
      }
    }
    getName()
  }, [instance, navigate])

  return(
    <>
      <header className="d-flex justify-content-between text-start p-3" style={{ backgroundColor: "rgb(120, 120, 140)" }}>
        {name}
        <Dropdown>
          <Dropdown.Toggle className='dropdown-toggle border-0'>
            <i class="fa-solid fa-bars text-dark"></i>
          </Dropdown.Toggle>
          <Dropdown.Menu>
            <Dropdown.Item>
              <Link to='/profile'>Profile</Link>
            </Dropdown.Item>
            <Dropdown.Item>
              <Link to='/contacts'>Contacts</Link>
            </Dropdown.Item>
            <Dropdown.Item>
              <Link to='/signout'>Sign out</Link>
            </Dropdown.Item>
          </Dropdown.Menu>
        </Dropdown>
      </header>
      <h1 className='text-center primary-text m-5'>Welcome {name}!</h1>
    </>
  )
}
