import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { Dropdown } from 'react-bootstrap'
import axios from 'axios'

export default function Profile (){
  const [name, setName] = useState('')
  const navigate = useNavigate()

  const instance = axios.create({
    baseURL: 'http://localhost:8000/',
    headers: {'Content-Type': 'application/json'},
    withCredentials: true
  })

  useEffect(()=>{ // Get the user name or redirect to sign in if not authenticated.
    const getName = async ()=>{
      const res = await instance.get('/profile')
      console.log(res.data)
      setName(res.data.fullname)
    }
    getName()
  }, [instance, navigate])
  return(
    <>
      <header className='dropdown-header'>
        <Dropdown>
          <Dropdown.Toggle className='dropdown border-0 text-dark' style={{background: 'transparent'}}>
            <i className="fa-solid fa-bars"></i>
          </Dropdown.Toggle>

          <Dropdown.Menu>
            <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
            <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
            <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
          </Dropdown.Menu>
        </Dropdown>  
      </header>


      <h1 className='text-center primary-text'>Welcome {name}!</h1>
    </>
  )
}
