import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'
import axios from 'axios'

export default function Profile (){
  const [name, setName] = useState('')
  const navigate = useNavigate()

  const instance = axios.create({
    baseURL: 'http://localhost:8000'
  })

  useEffect(()=>{ // Get the user name or redirect to sign in if not authenticated.
    const getName = async ()=>{
      const res = await instance.get('/signin')

      if (res.data.fullname === undefined) {
        navigate('/signin')
      }
      else {
        if (res.data.fullname[0].fullname === undefined) setName(res.data.fullname)
        else setName('back ' + res.data.fullname[0].fullname)
      }
    }
    getName()
  }, [instance, navigate])
  return(
    <>
      <h1 className='text-center text-white primary-text'>Welcome {name}!</h1>
    </>
  )
}
